const btnNav=document.querySelector(".btn-menu"),sidenav=document.querySelector(".sidenav");btnNav.addEventListener("click",(()=>{sidenav.classList.toggle("sidenav-open")}));const checkboxBtn=document.querySelector(".switch-btn"),body=document.querySelector(".body");checkboxBtn.addEventListener("click",(()=>{checkboxBtn.checked?body.classList.add("dark-theme"):body.classList.remove("dark-theme")}));